	public static String encode(String text){
		String s = "";
		char[] x = text.toCharArray();
		int[] t = new int[100];
		int u = 0;

		for(int i = 1; i <= 6; i++){
			for(int j = 7; j <= 12; j++){
				t[u] = i * j;
				u++;
			}
		}

		for(int i = 0; i < x.length; i++){
			int v = x[i] * t[i % 26];
			s += String.valueOf(v);
			if(i < x.length - 1){
				s += ", ";
			}
		}

		return s;
	}

	public static String decode(int[] x){
		String s = "";
		int[] t = new int[100];
		int u = 0;
		for(int i = 1; i <= 6; i++){
			for(int j = 7; j <= 12; j++){
				t[u] = i * j;
				u++;
			}
		}
		for(int i = 0; i < x.length; i++){
			int v = x[i] / t[i % 26];
			s += Character.toString((char) v);
		}
		return s.toString();
	}


